function countDownTime() {
    const coutDown = document.querySelector('.main3-flashsale-right');
    // thời gian 1 ngày
    let timeCountDown = 86400;

    setInterval(() => {
        let numberOfDays = Math.floor(timeCountDown / 86400);
        let numberOfHours = Math.floor((timeCountDown % 86400) / 3600);
        let numberOfMinutes = Math.floor((timeCountDown % 3600) / 60);
        let numberOfSeconds = timeCountDown % 60;
        let stringnumberOfDays = (numberOfDays < 10 ? "0" : numberOfDays) + numberOfDays.toString();
        let stringnumberOfHours = (numberOfHours < 10 ? "0" + numberOfHours.toString() : numberOfHours.toString());
        let stringnumberOfMinutes = (numberOfMinutes < 10 ? "0" + numberOfMinutes.toString() : numberOfMinutes.toString());
        let stringnumberOfSeconds = (numberOfSeconds < 10 ? "0" + numberOfSeconds.toString() : numberOfSeconds.toString());
        coutDown.innerHTML = `<span>${stringnumberOfDays[0]}</span>
                        <span>${stringnumberOfDays[1]}</span> :
                        <span>${stringnumberOfHours[0]}</span>
                        <span>${stringnumberOfHours[1]}</span> :
                        <span>${stringnumberOfMinutes[0]}</span>
                        <span>${stringnumberOfMinutes[1]}</span> :
                        <span>${stringnumberOfSeconds[0]}</span>
                        <span>${stringnumberOfSeconds[1]}</span>`
            ;
        // Giảm thời gian còn lại
        if (timeCountDown > 0) {
            timeCountDown--;
        } else {
            clearInterval(countdownInterval); // Dừng lại khi đếm ngược về 0
        }
    }, 1000);
}
