// Chức năng thêm vào giỏ hàng
function buttonAddListItem(idproducts, name) {
    switch (name) {
        case 'groupProducts':
            let itemProducts = flatcontentSlide.find((product) => product.id === idproducts);
            let cartData = localStorage.getItem('cart');
            let cart = cartData ? JSON.parse(cartData) : [];
            let testInLocalStorage = cart.find((product) => product.id === idproducts);
            if (testInLocalStorage) {
                testInLocalStorage.countQuantity++
            }
            else {
                itemProducts.countQuantity = 1;
                cart.push(itemProducts);
            };

            localStorage.setItem('cart', JSON.stringify(cart));
            renderList();
            alert("Sản phẩm đã được thêm vào giỏ hàng");
            break;
        case 'HotSellingProducts':
            let itemHotSellingProducts = allContentOfHotSaleProduct.find((product) => product.id === idproducts);
            let hotSellingCartData = localStorage.getItem('cart');
            let hotSellingCart = hotSellingCartData ? JSON.parse(hotSellingCartData) : [];

            let testInHotSellingLocalStorage = hotSellingCart.find((product) => product.id === idproducts);
            if (testInHotSellingLocalStorage) {
                testInHotSellingLocalStorage.countQuantity++;
            } else {
                itemHotSellingProducts.countQuantity = 1;
                hotSellingCart.push(itemHotSellingProducts);
            }
            localStorage.setItem('cart', JSON.stringify(hotSellingCart));
            renderList();
            alert("Sản phẩm đã được thêm vào giỏ hàng");
            break;
        default:
            console.log("Invalid Value")
    }
}

function renderList() {
    const listAdd = document.querySelector('.list-item-added');
    const tableListItem = document.querySelector('.tableBody');
    let data = JSON.parse(localStorage.getItem('cart')) || [];
    const itemCountQuantity = document.querySelector(".countQuantity");
    const tableHead = document.querySelector('.tableHead');
    const buttonDeleteAllItem = document.querySelector('.buttonDeleteAllItem');

    let count = 0;
    if (data.length === 0) {
        tableListItem.innerHTML = "";
        tableHead.style.display = 'none';
        buttonDeleteAllItem.style.display = 'none';
        tableListItem.innerHTML = `
        <img src="imgaes/emtycart.png"/>
        <p style="color: red; font-size: 0.9vw">Chưa có sản phẩm</p>`
    }
    else {
        tableListItem.innerHTML = '';
        buttonDeleteAllItem.style.display = 'block';
        tableHead.style.display = 'table-header-group';
        data.forEach((product) => {
            let tr = document.createElement('tr');
            tr.innerHTML = `
                                <td><span><img src=${product.img}><span>${product.title}</span></span></td>
                                <td><span>${product.countQuantity}</span></td>
                                <td><span>${"₫" + new Intl.NumberFormat('vi-VN').format((product.price.replace(/\./g, '')) * product.countQuantity)}</span></td>
                                `;
            tableListItem.appendChild(tr);
            count = count + product.countQuantity
        });
    }
    itemCountQuantity.innerHTML = `${count}`;
}

